#!/usr/bin/env python3
import glob
import os
from jinja2 import Environment, FileSystemLoader
env = Environment(loader=FileSystemLoader('./', encoding='utf8'))
tpl = env.get_template('ltp-fail.tmpl.yaml')

warn_message = '''
##################  DO NOT EDIT  ##################
#  This file generated by ltp-fail.generate.py.   #
#  You MUST NOT edit this file.                   #
###################################################
'''


# このテストは、カーネルがハングしてしまい、clustertestが完走しない。
# execveat03

# FAILしたテストケース
names = '''
fcntl24
fcntl24_64
fcntl25
fcntl25_64
fcntl26
fcntl26_64
fcntl33
fcntl33_64
'''.strip().splitlines()

# CONFで失敗したテストケース
names += '''
bdflush01
cacheflush01
fallocate01
fallocate03
fcntl06
fcntl06_64
getrusage04
'''.strip().splitlines()


# Remove generated files.
files = glob.glob('ltp-fail.generated.*.yaml')
for f in files:
    os.unlink(f)

# Generate files.
for name in names:
    tpl_data = {
        'r': {
            'vmname': name.replace('_', '-'),
            'cmd': name,
        },
        'warn': warn_message,
    }
    with open(f'ltp-fail.generated.{name}.yaml', 'w') as f:
        data = tpl.render(tpl_data)
        f.write(data)
